    Eigen::Matrix3d P;
    Eigen::Matrix3d F_transpose = F.transpose();
    Eigen::Matrix3d F_inverse_transpose = F_transpose.inverse();
    P = C * (F - F_inverse_transpose);

    double J = F.determinant();
    double log_J = std::log(J);
    P = P + D * log_J * F_inverse_transpose;

    // transform the shape of P from matrix to vector
    for (size_t i = 0; i < P.rows(); i++)
        for (size_t j = 0; j < P.cols(); j++) {
            auto rowIndex = static_cast<Eigen::Index>(i);
            auto colIndex = static_cast<Eigen::Index>(j);

            dpsi[rowIndex*P.cols() + colIndex] = P(rowIndex, colIndex);
        }

